mapper.select:
SELECT COUNT(*) FROM setmeal WHERE id IN
#{id}
AND status = 1

mapper.delete:
delete from setmeal
where id in

    #{id}

mapper.select:
select * from setmeal
where category_id = #{categoryId} and status = #{status}
order by update_time desc

select * from setmeal
where name = #{name}
order by update_time
    limit #{startIndex},#{pageSize}
